import React from 'react'
import SimpleImageSlider from "react-simple-image-slider";
import './hero.css'

const images = [
  { url: "https://t3.ftcdn.net/jpg/06/64/36/56/360_F_664365620_eGm4Qp7OhVkYltslzOSi4SnpJWJLTmne.jpg" },
  { url: "https://i.ytimg.com/vi/xDAuWErAy1c/maxresdefault.jpg" },
  { url: "https://m.economictimes.com/thumb/msid-83958385,width-1200,height-900,resizemode-4,imgsize-316001/1.jpg" },
  { url: "https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEiKfcDfTZJVfY2vZ7hdh0E9Uu07e4GxuDLkyXEmzgKNpIxuh84OhR47DHGETCh0jKOIIzWUCMZy3V3q8-UxwZZZ4iLixTHyiyJAYeypznXB7AX21EUEY-sQtpTvns1TpxwkQq1gzy3BWUfL/s0/stop-food-waste-day-rich-poor-hungry.jpg" },
];

function Hero() {
  return (
    <div >
        
      <SimpleImageSlider
     
        padding={0}
        margin={0}
        width={"100%"}
        height={"100vh"}
        margin-top={"113px"}
        images={images}
        showBullets={true}
        showNavs={true} 
     >
     <h1 style={{
          position: 'absolute',
          textAlign:center,
          top: '113px',
          left: '50%',
          transform: 'translateX(-50%)',
          color: 'white',
          zIndex: 1
        }}>
          Bringing smile to millions
        </h1>
      </SimpleImageSlider>
    </div>
  )

}
export default Hero;
