import React from 'react'
import SimpleImageSlider from "react-simple-image-slider";
import './hero.css'

const images = [
  { url: "https://t3.ftcdn.net/jpg/06/64/36/56/360_F_664365620_eGm4Qp7OhVkYltslzOSi4SnpJWJLTmne.jpg" },
  { url: "https://uttamaahar.org/wp-content/uploads/2021/07/Food-Waste.jpg" },
  { url: "htts://media.istockphoto.com/id/524903696/photo/poor-indian-children-asking-for-food-india.jpg?s=612x612&w=0&k=20&c=uAUDyZRdpReAW51hD29W7fOCTDzNrOdHkAdKXErbapU=" },
  { url: "https://www.unnatisilks.com/cdn/shop/articles/120614fp-anamrita-iskcon-6.jpg?v=1655286149&width=2048" },
];

function Hero() {
  return (
    <div className='hero'>
      
      <SimpleImageSlider>
      padding={0}
        margin={0}
        width={"100%"}
        height={"100vh"}
        margin-top={"113px"}
        images={images}
        showBullets={true}
        showNavs={true}
        <div className='hero-text'>
        <h1>Bringing smiles to Millions</h1>
        <div className='disflex'>
          <button>Donate Now</button>
          <button>Watch Video</button>
        </div>
      </div>
      </SimpleImageSlider>
        
      

    </div>
  )

}



export default Hero;
