import React from 'react'
import SimpleImageSlider from "react-simple-image-slider";
import food1 from './food1.jpg';
import food2 from './food2.jpg';
import food3 from './food3.jpg';
import Suvidhaabout from './Suvidhaabout.jpg';
import './hero.css'

const images = [
 { url:"https://uttamaahar.org/wp-content/uploads/2021/07/Food-Waste.jpg"},
 { url:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.freepik.com%2Fpremium-ai-image%2Fgroup-children-rural-village-their-smiles-innocence-shining-through-despite-their-hars_63353426.htm&psig=AOvVaw15XLk_mFCUkNNy4r0PGOW4&ust=1723113548282000&source=images&cd=vfe&opi=89978449&ved=0CBEQjRxqFwoTCNCrr7vX4ocDFQAAAAAdAAAAABAE"},
 { url:"data:image/jpeg;base64,"},
 { url:""},
];

function Hero() {
  return (
    <div>
    <SimpleImageSlider
       padding={0}
       margin={0}
        width={"100%"}
        height={"100vh"}
        margin-top={"113px"} 
        images={images}
        showBullets={true}
        showNavs={true}
        
      />
      
    <div className='hero'>
      
      <div className='hero-text'>
        <h1>Bringing smiles to Millions</h1>
        <div className='disflex'>
          <button>Donate Now</button>
          <button>Watch Video</button>
        </div>
      </div>
    </div>
</div>
  )

}



export default Hero;
