import React from 'react'
import SimpleImageSlider from "react-simple-image-slider";
import food1 from './food1.jpg';
import food2 from './food2.jpg';
import food3 from './food3.jpg';
import Suvidhaabout from './Suvidhaabout.jpg';
import './hero.css'

const images = [
  <img src={food1} alt='' />,
  <img src={food2} alt='' />,
  <img src={food3} alt='' />,
  <img src={Suvidhaabout} alt='' />
];

function Hero() {
  return (
    <div className='hero'>
    <SimpleImageSlider
        width={"100%"}
        height={504}
        images={images}
        showBullets={true}
        showNavs={true}
      />
    <div className='hero'>
      
      <div className='hero-text'>
        <h1>Bringing smiles to Millions</h1>
        <div className='disflex'>
          <button>Donate Now</button>
          <button>Watch Video</button>
        </div>
      </div>
    </div>
</div>
  )

}



export default Hero;
